package Pratice;

public class Series_Odd_Numbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=100;
		for( i=0;i<=101;i++)
		{
			
			if(i%2==0)
				System.out.println("Number is Even--"+i);
			
			else
				System.out.println("Number is odd--"+i);
		}
		
		
		

	}

}
